import { createContext, useState, useEffect } from 'react'


const FeedbackContext = createContext()

export const FeedbackProvider = ( {children} ) => {
    const [feedback, setFeedback] = useState([])
    const [isLoading, setIsLoading] = useState(true)
    const [feedbackEdit, setFeedbackEdit] = useState(
        {
            item: {},
            edit: false,
        }
    )

    useEffect(() => {fetchFeedback()}, [])

    const fetchFeedback = async () => {
        const response = await fetch('http://localhost:5000/feedback')
        const data = await response.json()
        setFeedback(data)
        setIsLoading(false)
    }

    const deleteFeedback = (id) => {
        if (window.confirm('Are you sure you want to delete?')) {
            setFeedback(feedback.filter((item) => item.id !== id))
        }   
    }

    const updateFeedback = (id, updItem) => {setFeedback(feedback.map((item) => item.id === id ? {...item, ...updItem} : item))}

    const addFeedback = async (newFeedback) => {
        const response = await fetch('http://localhost:5000/feedback', {
            method: 'POST',
            headers: {'Content-Type': 'application/json'},

            body: JSON.stringify(newFeedback)
        })

        const data = await response.json()
        setFeedback([data, ...feedback])
    }

    const editFeedback = (item) => {
        setFeedbackEdit({
        item,
        edit: true
    })}

    return (
        <FeedbackContext.Provider value={{ feedback, feedbackEdit, isLoading, deleteFeedback, addFeedback, editFeedback, updateFeedback }}>
            {children}
        </FeedbackContext.Provider>
    )
}

export default FeedbackContext
